export const numberFormat = function (value) {
	const k = 10000
	const sizes = ['', '万', '亿', '万亿']
	let i
	let param = {}
	if(value < k){
		param.value =value
		param.unit=''
	}else{
		// log 方法可返回一个数的自然对数。
		i = Math.floor(Math.log(value) / Math.log(k))
		// pow方法可返回 x 的 y 次幂的值。
		param.value = ((value / Math.pow(k, i))).toFixed(2)
		param.unit = sizes[i];
	}
	return param.value + param.unit
}
